						break;
					case "65978d40fdfebd55d412343d":
					case "FileSizeTest":
						visitedSchemas.Add("65978d40fdfebd55d412343d");
						visitedSchemas.Add("FileSizeTest");

						if (!gameDataCollections.TryGetValue("65978d40fdfebd55d412343d", out gameDataDocumentCollection)) {
